import axios from "axios";
import { useParams } from "react-router-dom";

// Set Source loading
export const setproductLoading = () => {
  return {
    type: "SET_PRODUCT_LOADING",
  };
};

export const setproducts = () => async (dispatch) => {
  dispatch(setproductLoading());
  try {
    const newsSource = await axios.get(`https://fakestoreapi.com/products`);
    // console.log('newsOurce=======>>>>>>', newsSource);
    if (newsSource) {
      dispatch({
        type: "SET_PRODUCTS",
        payload: newsSource.data,
      });
      console.log('setproducts Action======>>>>>>', newsSource.data);
    }
  } catch (err) {
    // dispatch({
      //   type: NEWS_SOURCE_ERROR,
      // });
      console.log("err", err);
    }
  };

export const selectedproduct = () => async (dispatch) => {
  const { productId } = useParams();
  // return {
  //   type: "SELECTED_PRODUCTS",
  //   payload: products,
  // };
  try {
    // const newsSource = await axios.get(`https://fakestoreapi.com/products/${productId}`);
    const newsSource = await axios.get(`https://fakestoreapi.com/products/1`);
    // console.log('newsOurce=======>>>>>>', newsSource);
    if (newsSource) {
      dispatch({
        type: "SELECTED_PRODUCTS",
        payload: newsSource.data,
      });
      console.log('setproducts Action======>>>>>>', newsSource.data);
    }
  } catch (err) {
    // dispatch({
      //   type: NEWS_SOURCE_ERROR,
      // });
      console.log("err", err);
    }
};
