import React, { Component } from 'react'
// import LifecycleB from './LifecycleB'

export default class LifecycleA extends Component {
	constructor(props) {
		super(props)
		this.state = {
			name: 'Hello',
			counter: 0,
			data: []
		}
		console.log('LifecycleA constructor')
	}

	handelClick = () => {
		this.setState({
			counter: this.state.counter + 1
		})
	}

	static getDerivedStateFromProps(props, state) {

		console.log('LifecycleA getDerivedStateFromProps', state)
		return null
	}

	componentDidMount() {
		setTimeout(() => {
			this.setState({ name: "This is a componentDidMount Method" })
			fetch('https://jsonplaceholder.typicode.com/users').then(
				(response) => response.json()
			).then(
				data => this.setState({ data: data })
			)

		}, 2000)

		// fetch('https://jsonplaceholder.typicode.com/users').then(
		// 	(response) => response.json()
		// ).then(data => this.setState({ data: data }))

		console.log('LifecycleA componentDidMount')
	}

	shouldComponentUpdate() {
		console.log('LifecycleA shouldComponentUpdate')
		return true
		// if (this.state.counter >= 1) {
		// 	return true
		// } else {
		// 	return false
		// }
	}
	getSnapshotBeforeUpdate(prevProps, prevState) {
		console.log('LifecycleA getSnapshotBeforeUpdate')
		return null
	}

	componentDidUpdate(prevProps, prevState, snapshot) {
		// debugger
		console.log('LifecycleA componentDidUpdate method called', prevState)
		// if (prevState.counter === this.state.counter) {
		// 	console.log("Counter match")
		// }
	}

	render() {

		console.log('LifecycleA render')
		return (
			<div>
				<button onClick={this.handelClick} >Update Counter</button>
				{/* <p>{this.state.counter}</p> */}
				<Child data={this.state.counter} />

				<p>{this.state.name}</p>
				{this.state.data.map(d =>
					<h6 key={d.id}>
						{d.name}
					</h6>
				)}

				This is a LifecycleA
				{/* <LifecycleB nameFromParent="getDerivedStateFromProps Method" /> */}
			</div>
		)
	}
}
