export const selectedproduct = () => async (dispatch) => {
  dispatch(setSourceLoading());
  try {
    const newsSource = await axios.get(`https://fakestoreapi.com/products`);
    if (newsSource) {
      dispatch({
        type:  "SELECTED_PRODUCTS",
        payload: newsSource.data,
      });
    }
  } catch (error) {
    // dispatch({
    //   type: NEWS_SOURCE_ERROR,
    // });
    console.log("err", err);
  }
};
