import React, { useEffect, useState, useRef } from "react";
import {
  Card,
  Divider,
  Col,
  Row,
  Space,
  Table,
  Form,
  Button,
  Typography,
} from "antd";
import Formitem from "./Formitem";
import { useDispatch, useSelector } from "react-redux";
import { bindActionCreators } from "redux";
import { actionCreators } from "../../redux/index.js";

const Formtext = () => {
  const formFiled = {
    firstname: {
      title: "First name",
      type: "text",
      name: "firstname",
      placeholder: "Enter your firstname",
    },

    lastname: {
      title: "Last name",
      type: "text",
      name: "lastname",
      placeholder: "Enter your lastname",
    },

    middlename: {
      title: "middlename",
      type: "text",
      name: "middlename",
      placeholder: "Enter your middlename",
    },
    email: {
      title: "Email",
      type: "email",
      name: "email",
      placeholder: "Enter your email",
    },
    mobile: {
      title: "Mobile",
      type: "number",
      name: "mobile",
      placeholder: "Enter your mobile",
    },

    hobby: {
      title: "Hobby",
      type: "checkbox",
      name: "hobby",

      hobbyList: [
        {
          name: "Cricket",
          value: "cricket",
          type: "checkbox",
          checked: false,
        },
        {
          name: "Readingbook",
          value: "readingbook",
          type: "checkbox",
          checked: false,
        },
        {
          name: "Televison",
          value: "televison",
          type: "checkbox",
          checked: false,
        },
        {
          name: "Fashion",
          value: "fashion",
          type: "checkbox",
          checked: false,
        },
      ],
    },
    radio: {
      title: "Gender",
      type: "radio",
      name: "gender",

      radioList: [
        {
          label: "Male",
          value: "male",
          checked: false,
        },
        {
          label: "Female",
          value: "female",
          checked: false,
        },
        {
          label: "Other",
          value: "other",
          checked: false,
        },
      ],
    },
    countryList: {
      title: "Country",
      type: "select",
      name: "country",

      countries: [
        {
          name: "Germany",
          states: [
            {
              name: "A",
              cities: ["Duesseldorf", "Leinfelden-Echterdingen", "Eschborn"],
            },
          ],
        },
        { name: "Spain", states: [{ name: "B", cities: ["Barcelona"] }] },

        { name: "USA", states: [{ name: "C", cities: ["Downers Grove"] }] },
        {
          name: "Mexico",
          states: [{ name: ["D", "F", "H"], cities: ["Puebla"] }],
        },
        {
          name: "India",
          states: [
            { name: "E", cities: ["Delhi", "Kolkata", "Mumbai", "Bangalore"] },
          ],
        },
      ],
    },
  };
  const columns = [
    {
      title: "Firstname",
      dataIndex: "firstname",
      key: "firstname",
    },
    {
      title: "Lastname",
      dataIndex: "lastname",
      key: "lastname",
    },
    {
      title: "Middlename",
      dataIndex: "middlename",
      key: "middlename",
    },
    {
      title: "Email",
      dataIndex: "email",
      key: "email",
    },
    {
      title: "Mobile",
      key: "Mobile",
      dataIndex: "mobile",
    },
    {
      title: "Hobby",
      key: "hobby",
      dataIndex: "hobby",
    },
    {
      title: "Radio",
      key: "radio",
      dataIndex: "radio",
    },
    {
      title: "Country",
      key: "country",
      dataIndex: "country",
    },
    {
      title: "Action",
      render: (_, record) => {
        return (
          <span>
            <Space>
              <Typography.Link onClick={() => onHandleEdit(record)}>
                Edit
              </Typography.Link>

              <Typography.Link onClick={() => deleteData(record)}>
                Delete
              </Typography.Link>
            </Space>
          </span>
        );
      },
    },
  ];

  const dispatch = useDispatch();
  const { addItemAction, deleteItemAction, updateItemAction } = bindActionCreators(actionCreators, dispatch);

  const [status, setStatus] = useState({
    edit: false,
    add: false,
  });

  const onHandleEdit = (record) => {
    const { id, firstname, lastname, mobile, email } = record;
    setformData({
      ...record,
      id: id,
    });
    console.log('onHandeledit=======>>>>>>>>>', { ...record });
    setStatus({ ...status, edit: true });
  };

  const cancelUpdate = () => {
    setformData({});
    setError({});
  };

  const deleteData = (record) => {
    deleteItemAction(record.id);
    setformData({});
    setError({});
  };

  const [formData, setformData] = useState({
    firstname: "",
    lastname: "",
    middlename: "",
    mobile: "",
    email: "",
  });

  useEffect(() => {
    const group = localStorage.setItem("items", JSON.stringify(formData));
    console.log('group=====>>>>>', group);
  }, [formData]);

  useEffect(() => {
    const items = JSON.parse(localStorage.getItem("items"));
    console.log('setformData items=====>>>>>', items);
    if (items) {
      setformData(items);
    }
  }, []);

  const [errors, setError] = useState({});
  const getRedux = useSelector((state) => state.addItem);

  const datasource = getRedux?.map((get) => {
    return {
      id: get.id,
      firstname: get.formData.firstname,
      lastname: get.formData.lastname,
      email: get.formData.email,
      mobile: get.formData.mobile,
      hobby: get.formData.hobbies.toString(),
      radio: get.formData.gender,
      country: get.formData.countryName,
      middlename: get.formData.middlename,
    };
  });
  console.log('show table data from redux ==>>>>>>>>>', getRedux);

  const formRef = useRef();

  const handleChange = (e) => {
    setformData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleCountry = (countryName) => {
    setformData({
      ...formData,
      countryName,
    });
  };

  const handleCheck = (hobbies) => {
    setformData({
      ...formData,
      hobbies: hobbies,
    });
  };

  // const handleChanges = (event) => {
  //   if (event.target.checked) {
  //     setformData({
  //       ...formData,
  //       hobbies: event.target.checked,
  //     });
  //     console.log('✅ Checkbox is checked');
  //   } else {
  //     console.log('⛔️ Checkbox is NOT checked');
  //   }
  // };

  const checkEmail = (email) => {
    var reg = /^([A-Za-z0-9_\-\.])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,4})$/;
    if (!reg.test(email)) {
      return true;
    } else {
      return false;
    }
  };

  const onFinish = ({ user }) => {
    console.log('onFINISH====>>>>>>>>>>>');
    if (!user.hobby?.length || !user.gender || checkEmail(user.email)) {
      setError({
        hobbiesError: Boolean(!user.hobby?.length),
        genderError: Boolean(!user.gender),
        emailError: checkEmail(user.email)
      })
    } else {
      if (status.edit) {
        updateItemAction(formData);
        setformData({});
        setError({});
        formRef.current.resetFields();
        setStatus({ ...status, edit: false });
      } else {
        addItemAction({
          id: new Date().getTime().toString(),
          formData,
        });
        setformData({});
        setError({});
        formRef.current.resetFields();
      }
    }
  };

  return (
    <>
      <Row>
        <Card title="Form" style={{ width: 393, backgroundColor: "#f5f5f5" }}>
          <Form
            layout="vertical"
            name="form"
            initialValues={{ remember: true }}
            onFinish={onFinish}
            // onFinishFailed={onFinishFailed}
            autoComplete="off"
            ref={formRef}
            style={{ backgroundColor: "#f5f5f5" }}
          >
            {/* <Col span={24}> */}
            <Formitem
              formFiled={{ ...formFiled.firstname, value: formData.firstname }}
              handleChange={handleChange}
            />
            <Formitem
              formFiled={{ ...formFiled.lastname, value: formData.lastname }}
              handleChange={handleChange}
            />
            <Formitem
              formFiled={{ ...formFiled.mobile, value: formData.mobile }}
              handleChange={handleChange}
            />
            <Formitem
              formFiled={{ ...formFiled.email, value: formData.email }}
              formError={errors}
              handleChange={handleChange}
            />
            <Formitem
              formFiled={{ ...formFiled.countryList, value: formData.country }}
              handleCountry={handleCountry}
            />
            <Formitem
              formFiled={{
                ...formFiled.middlename,
                value: formData.middlename,
              }}
              requireded={false}
              handleChange={handleChange}
            />
            <Formitem
              formFiled={{
                ...formFiled.hobby,
                value: formData.hobby?.split(","),
              }}
              handleCheck={handleCheck}
            />
            <Formitem
              formFiled={formFiled.radio}
              formData={formData}
              formError={errors}
              handleRadio={handleChange}
            />

            {!status.edit ? (
              <Form.Item>
                <Button type="primary mx-2" htmlType="submit">
                  Submit
                </Button>
              </Form.Item>
            ) : (
              <Form.Item>
                <Button type="primary mx-2" htmlType="submit">
                  update
                </Button>
                <Button
                  type="primary mx-2"
                  htmlType="submit"
                  onClick={cancelUpdate}
                >
                  cancel
                </Button>
              </Form.Item>
            )}
          </Form>
        </Card>
        {/* <Col span={1}></Col> */}
        <Divider
          type="vertical"
          style={{ height: "900px", backgroundColor: "rgb(234 224 224)" }}
        />
        <Col span={18}>
          <Table columns={columns} dataSource={datasource}></Table>
        </Col>
      </Row>
    </>
  );
};

export default Formtext;
