import { Box, Button, TextField } from "@material-ui/core";
import { React, useState } from "react";
import ToDoListItem from "./ToDoListItem";
import AddIcon from "@mui/icons-material/Add";

const ToDoItem = () => {
  const [inputList, setInputList] = useState("");
  const [Items, setItems] = useState([]);
  const [IsHovering, setIsHovering] = useState(false);

  const handleMouseOver = () => {
    setIsHovering(true);
  };
  const handleMouseIn = () => {
    setIsHovering(false);
  };

  const itemEvent = (event) => {
    console.log("itemEvent fire ==>>", event.target.value);
    setInputList(event.target.value);
  };
  const listOfItems = () => {
    console.log("ListOfItems fire=..", inputList);
    setItems((OldItems) => {
      return [...OldItems, inputList];
    });
    setInputList("");
  };
  const deleteItems = (id) => {
    setItems((OldItems) => {
      return OldItems.filter((arrElem, index) => {
        console.log("value of deleteItems==>>>", index);
        return index !== id;
      });
    });
  };

  return (
    <div className="main_div">
      <div className="center_div">
        <br />
        <h1>Note List</h1>
        <br />
        <Box sx={{ flexGrow: 1 }}>
          <TextField
            placeholder="Add a Notes"
            value={inputList}
            onChange={itemEvent}
          />

          <Button
            style={{
              // backgroundColor: IsHovering ? "orange" : "#ff4757",
              cursor: "pointer",
              backgroundColor: IsHovering ? "green" : "",
            }}
            onClick={listOfItems}
            onMouseEnter={handleMouseOver}
            onMouseLeave={handleMouseIn}
          >
            <AddIcon />
          </Button>

          <ol>
            {Items.map((itemval, index) => {
              return (
                <ToDoListItem
                  key={index}
                  id={index}
                  Itemlist={itemval}
                  ItemlistDelete={deleteItems}
                />
              );
            })}
          </ol>
        </Box>
      </div>
    </div>
  );
};
export default ToDoItem;
