const intialState = {
  products: [],
  productLoading:false
};

const productReducer = (state = intialState,  action ) => {
  const { type, payload } = action;
  switch (type) {
    case "SELECTED_PRODUCTS": {
      return {
        ...state,
        products: payload
      };
    }
    default:
      console.log('SET_PRODUCTS Reducer====>>>>',state);
      return state;
    }
  };

export default productReducer;
