import React, { useState } from "react";
import Alert from "./Alert";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import Formtext from "./features/post/Formtext";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useSelector } from "react-redux";
import ProductComponent from "./components/products/ProductComponent";

function App() {
  const [mode, setmode] = useState("light"); // whether dark mode is enabled or not.
  const [alert, setAlert] = useState(null); // showMessage alert

  const showAlert = (message, type) => {
    setAlert({
      msg: message,
      type: type,
    });
    setTimeout(() => {
      setAlert(null);
    }, 3000);
  };
  // setTimeout(() => {
  //   msg : message,
  //   type : type
  // }, 1000);

  const toggleMode = () => {
    if (mode === "light") {
      setmode("dark");
      document.body.style.backgroundColor = "#042743";
      showAlert("Dark mode has beeen enabled", "success");
      document.title = "TextUtility - Dark Mode";
    } else {
      setmode("light");
      document.body.style.backgroundColor = "white";
      showAlert("Light mode has been enabled", "success");
      document.title = "TextUtility - Light Mode";
    }
  };

  const submitHandlerEvent = (event) => {
    console.log("on submit handler", event);
  };

  const templete = {
    title: "Registration Form",
    fields: [
      {
        title: "First name",
        type: "text",
        name: "firstname",
        placeholder: "Enter your firstname",
      },
      {
        title: "Last name",
        type: "text",
        name: "lastname",
        placeholder: "Enter your lastname",
      },
      {
        title: "Email",
        type: "email",
        name: "email",
        placeholder: "Enter your email",
      },
      {
        title: "Password",
        type: "password",
        name: "password",
        placeholder: "Enter your password",
      },
      {
        title: "ConfirmPassword",
        type: "password",
        name: "confirmPassword",
        placeholder: "Enter your confirmPassword",
      },
    ],
  };

  return (
    <>
      <Router>
        <Navbar title="TextUtils" mode={mode} toggleMode={toggleMode} />
        <Alert alert={alert} />
        <div className="col-md-12 my-3 ms-5">
          <Routes>
            <Route exact path="/about" element={<ProductComponent mode={mode} />} />
            <Route
              exact
              path="/"
              element={
                <TextForm
                  showAlert={showAlert}
                  heading="Enter the text to analyze below"
                  mode={mode}
                />
              }
            />
            {/* <Route exact path="/form"  element={<Regform templete={templete} submitHandler={submitHandlerEvent} />}/> */}
            <Route exact path="/form" element={<Formtext />} />
          </Routes>
        </div>
      </Router>
    </>
  );
}
// ${props.mode === 'light' ? 'dark' : 'light'}
export default App;
