import { refType } from "@mui/utils";
import React, { useState } from "react";

const Regform = ({ templete, submitHandler }) => {
  // const [input, setInput] = useState('');

  // const handleSubmit = (e) => {
  //   console.log("handleClick  clicked ", e.target.value)

  // }
  // const data = { firstname: "", lastname: "" }
  const [inputData, setinputData] = useState(templete);
  const [formData, setFormData] = useState({});

  console.log(inputData, formData);
  const handledata = (e) => {
    // const inputIndex = inputData.fields.findIndex(iD => iD.name == e.target.name)
    // inputData.fields[inputIndex].value = e.target.value;
    // setinputData({ ...inputData })

    // console.log(inputData);
    // const inputIndex = inputData.fields.findIndex(iD => iD.name == e.target.name)
    // inputData.fields[inputIndex].value = e.target.value;
    // setinputData({ ...inputData })

    const Json = { [e.target.name]: "dhiral" };
    const object1 = Json;
    console.log("object========>>>>>>>", object1);

    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  let { title, fields } = inputData;

  const renderfileds = (fields) => {
    return fields.map((field) => {
      let {
        title,
        type,
        name,
        placeholder,
        value = "",
        formStyle = "",
      } = field;
      return (
        <div key={name}>
          <label
            className="form__label"
            style={{ display: "block" }}
            htmlFor={name}
          >
            {title}{" "}
          </label>
          <input
            className="form__input"
            type={type}
            name={name}
            id={name}
            placeholder={placeholder}
            value={formData[name]}
            onChange={handledata}
          />
        </div>
      );
    });
  };

  return (
    <div>
      <h3>{title}</h3>

      <div className="mb-3">{renderfileds(fields)}</div>
      <div className="footer">
        <button onClick={submitHandler} className="btn btn-primary">
          Register
        </button>
      </div>
    </div>
  );
};

export default Regform;
