import axios from "axios";
import { React, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { selectedproduct } from "../../redux/actionCreators/productActions";

const ProductDetail = () => {
  const product = useSelector((state) => state.allproducts.products);
  const { id, image, title, price, category, description } = product;
  console.log('ProductDetail productId=======>', product);
  const { productId } = useParams();
  const dispatch = useDispatch();

  // const fetchproductDetail = async () => {
  //   const responce = await axios.get(`https://fakestoreapi.com/products/${productId}`).catch(err => {
  //     console.log('axiosFetch error', err);
  //   })
  //   console.log('axiosFetch responce', responce);
  //   dispatch(selectedproduct(responce.data))
  // }

  // useEffect(() => {
  //   if (productId && productId !== '') fetchproductDetail();
  // }, [productId])

  useEffect(() => {
    return () => {
      dispatch(selectedproduct());
      // fetchProducts();
    };
  }, []);
  return (
    <Grid
      container
      spacing={4}
      py={3}
      key={product.id}
      style={{ display: "flex" }}
    >
      <Card className="animate__animated animate__fadeIn" raised>
        <CardActionArea>
          <Link href={`/product/${id}`}>
            <CardMedia
              component="img"
              height="260"
              image={image}
              alt={id}
            // onClick={handleNavigation}
            />
            <CardContent>
              <Typography variant="body2" color="text.secondary" noWrap>
                {title}
              </Typography>
            </CardContent>
          </Link>
        </CardActionArea>
        <CardActions sx={{ display: "flex", justifyContent: "space-around" }}>
          <Button
            size="small"
            color="error"
          //  onClick={handleNavigation}
          >
            {/* <Link href={`/product/${id}`}> */}
            View more
            {/* </Link> */}
          </Button>
          <Typography
            variant="subtitle2"
            color="text.secondary"
            align="right"
          >
            {`$${price}`}
          </Typography>
        </CardActions>
      </Card>
    </Grid>
  );
}

export default ProductDetail;
