import axios from "axios";
import { React, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { selectedproduct } from "../../redux/actionCreators/productActions";

const ProductDetail = () => {
  const { productId } = useParams();
  const dispatch = useDispatch();
  const product = useSelector((state) => state.product);
  console.log('ProductDetail productId=======>', product);

  const fetchproductDetail = async () => {
    const responce = await axios.get(`https://fakestoreapi.com/products/${productId}`).catch(err => {
      console.log('axiosFetch error', err);
    })
    console.log('axiosFetch responce', responce);
    dispatch(selectedproduct(responce.data))
  }
  useEffect(() => {
    return () => {
      fetchproductDetail();
    }
  }, [])
  return (
    <div>

      <h2>add</h2>
    </div>
  );
}

export default ProductDetail;
