import axios from "axios";
import { Avatar, Card, IconButton, Link } from "@material-ui/core";
// import {
//   Button,
//   CardActionArea,
//   CardActions,
//   CardContent,
//   CardHeader,
//   CardMedia,
//   Collapse,
//   Grid,
//   Typography,
// } from "@mui/material";
import { React, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { selectedproduct } from "../../redux/actionCreators/productActions";

const ProductDetail = () => {
  const product = useSelector((state) => state.allproducts.products);
  const { id, image, title, price, category, description } = product;
  console.log('ProductDetail productId=======>', product);
  const { productId } = useParams();
  const dispatch = useDispatch();

  // const fetchproductDetail = async () => {
  //   const responce = await axios.get(`https://fakestoreapi.com/products/${productId}`).catch(err => {
  //     console.log('axiosFetch error', err);
  //   })
  //   console.log('axiosFetch responce', responce);
  //   dispatch(selectedproduct(responce.data))
  // }

  // useEffect(() => {
  //   if (productId && productId !== '') fetchproductDetail();
  // }, [productId])

  useEffect(() => {
    return () => {
      dispatch(selectedproduct());
      // fetchProducts();
    };
  }, []);
  return (

    <div className="ui grid container">
      {Object.keys(product).length === 0 ? (
        <div>...Loading</div>
      ) : (
        <div className="ui placeholder segment">
          <div className="ui two column stackable center aligned grid">
            <div className="ui vertical divider">AND</div>
            <div className="middle aligned row">
              <div className="column lp">
                <img className="ui fluid image" src={image} />
              </div>
              <div className="column rp">
                <h1>{title}</h1>
                <h2>
                  <a className="ui teal tag label">${price}</a>
                </h2>
                <h3 className="ui brown block header">{category}</h3>
                <p>{description}</p>
                <div className="ui vertical animated button" tabIndex="0">
                  <div className="hidden content">
                    <i className="shop icon"></i>
                  </div>
                  <div className="visible content">Add to Cart</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>

  );
}

export default ProductDetail;
